clear; close all; clc;dirs = readdir('corpus/');emailFiles = [];for i = 1:length(dirs)  if strncmp(dirs(i), '.', 1) != 1,    dir = strcat('corpus/', dirs(i), '/');    emails = readdir(dir);    for j = 1:length(emails)      if strncmp(emails(j), '.', 1) != 1,        if length(emailFiles) == 0,          emailFiles = [strcat(dir, emails(j))];        else,          emailFiles = [emailFiles; strcat(dir, emails(j))];        end;      end;    end;   end;end;X = [];for i = 1:10  file = cell2mat(emailFiles(i));  filecontents = readFile(file);  word_indices = processEmail(filecontents);  x = emailFeatures(word_indices);  X = [X; x'];  %for j = 1:length(filecontents)  %  if isfield(wordList, filecontents(j)),  %    wordList.(filecontents(j)) += 1;  %  else,  %    wordList.(filecontents(j)) = 0;  %  end;  %end;end;train_size = round(length(X(:, 1)) * 0.6);crossval_size = round(length(X(:, 1)) * 0.8);printf("%f, %f\n", 0, train_size);printf("%f, %f\n", (train_size+1), crossval_size);printf("%f, %f\n", (crossval_size+1), length(train_size));